i=imread('D:/Master student/2/PPP/pic/testImage.jpg');yimn=-1;S=zeros(3,3);S(1,:)=yimn;S(3,:)=yimn;S(:,1)=yimn;S(:,3)=yimn;S(2,2)=9;%CROP PINGVIk=i(200:480,520:777,:);%figure;%imshow(k);%rR=x(:,:,1);%rG=x(:,:,2);%rB=x(:,:,3);x_d=im2double(k);gause = fspecial ('Gaussian',5,5);%Y=conv2(x_d,gause);%Z=conv2(x_d,S);for i=1:3  xleb=x_d(:,:,i);  ig(:,:,i)=conv2(xleb,gause);  ir(:,:,i)=conv2(xleb,S);  iO(:,:,i)=xleb*1.488;  Io(:,:,i)=xleb*0.8841;endforfigure;  subplot(2,2,1);  imshow(ig);  subplot(2,2,2);  imshow(ir);  subplot(2,2,3);  imshow(iO);  subplot(2,2,4);  imshow(Io);  figure;imshow(k);